scripts.generate-module:
node -e "const n=process.argv[1];if(!n){console.error('Usage: npm run generate-module <name>');process.exit(1);}const{execSync}=require('child_process');const fs=require('fs');const path=require('path');execSync('nest g module '+n+' && nest g service '+n+' && nest g controller '+n,{stdio:'inherit'});const cap=n.charAt(0).toUpperCase()+n.slice(1);const repoPath=path.join('src',n,n+'.repository.ts');const dtoPath=path.join('src',n,n+'.dto.ts');const repoTemplate=`import { PrismaService } from '@/infrastructure/database/prisma.service';
import { Prisma } from '@generated/prisma/client';
import { Injectable } from '@nestjs/common';

@Injectable()
export class ${cap}Repository {
  constructor(private readonly prisma: PrismaService) {}

  async findUnique(
    where: Prisma.${cap}WhereUniqueInput,
    include?: Prisma.${cap}Include,
  ) {
    return this.prisma.${n}.findUnique({
      where,
      include,
    });
  }

  async findFirst(
    where: Prisma.${cap}WhereInput,
    include?: Prisma.${cap}Include,
  ) {
    return this.prisma.${n}.findFirst({
      where,
      include,
    });
  }

  async findMany(params?: {
    skip?: number;
    take?: number;
    cursor?: Prisma.${cap}WhereUniqueInput;
    where?: Prisma.${cap}WhereInput;
    orderBy?: Prisma.${cap}OrderByWithRelationInput;
    include?: Prisma.${cap}Include;
  }) {
    return this.prisma.${n}.findMany(params);
  }

  async create(data: Prisma.${cap}CreateInput, include?: Prisma.${cap}Include) {
    return this.prisma.${n}.create({
      data,
      include,
    });
  }

  async update(
    where: Prisma.${cap}WhereUniqueInput,
    data: Prisma.${cap}UpdateInput,
  ) {
    return this.prisma.${n}.update({
      where,
      data,
    });
  }

  async upsert(
    where: Prisma.${cap}WhereUniqueInput,
    create: Prisma.${cap}CreateInput,
    update: Prisma.${cap}UpdateInput,
  ) {
    return this.prisma.${n}.upsert({
      where,
      create,
      update,
    });
  }

  async delete(where: Prisma.${cap}WhereUniqueInput) {
    return this.prisma.${n}.delete({
      where,
    });
  }

  async deleteMany(where: Prisma.${cap}WhereInput) {
    return this.prisma.${n}.deleteMany({
      where,
    });
  }

  async count(where?: Prisma.${cap}WhereInput) {
    return this.prisma.${n}.count({
      where,
    });
  }
}
`;const dtoTemplate=`import { IsDate, IsString } from 'class-validator';

export class ${cap}Dto {
  @IsString()
  id: string;

  @IsDate()
  createdAt: Date;

  @IsDate()
  updatedAt: Date;

  // TODO: Add your specific fields here

  constructor(partial: Partial<${cap}Dto>) {
    Object.assign(this, partial);
  }
}
`;fs.writeFileSync(repoPath,repoTemplate);fs.writeFileSync(dtoPath,dtoTemplate);console.log('Repository created at:',repoPath);console.log('DTO created at:',dtoPath)"
